net.rptools.maptool.model.MacroButtonPro.command:
[h: toSend = '{}']
[h: argTest = json.type(arg(0))]
[h, if(argTest=='ARRAY'), code:
{
    [h: toWho = 'list']
    [h: toSend = json.set(toSend, 'mlOutputList', arg(0))]
};{
    [h: toWho = arg(0)]
}]

[h: toSend = json.set(toSend, 'toSend', arg(1))]
[h: conditionalOutput = macroLinkText('dbxConditionalOutput@this', toWho, toSend)]
[h: execLink(conditionalOutput, 1)]

[h:dbArgs = macro.args]
[h,if(json.contains(dbArgs,"Clear")),CODE:
{
   [h:dbArgs = json.set("{}", "mod", 0, "tn", 0, "d4s", 0, "d6s", 0, "d8s", 0, "d10s", 0, "d12s", 0, "d20s", 0, "d100s", 0, "showTo", "all", "breakdown", "totalRoll")]
   [macro("NDB@this"): dbArgs]
}]
[h,if(json.contains(dbArgs,"Roll")),CODE:
{
    [macro("RollDice@this"):dbArgs] 
    [abort(0)]
};
{
[if(json.contains(dbArgs,"d4btn")): dbArgs = json.set(dbArgs, "d4s", json.get(dbArgs, "d4s")+1)]
[if(json.contains(dbArgs,"d6btn")): dbArgs = json.set(dbArgs, "d6s", json.get(dbArgs, "d6s")+1)]
[if(json.contains(dbArgs,"d8btn")): dbArgs = json.set(dbArgs, "d8s", json.get(dbArgs, "d8s")+1)]
[if(json.contains(dbArgs,"d10btn")): dbArgs = json.set(dbArgs, "d10s", json.get(dbArgs, "d10s")+1)]
[if(json.contains(dbArgs,"d12btn")): dbArgs = json.set(dbArgs, "d12s", json.get(dbArgs, "d12s")+1)]
[if(json.contains(dbArgs,"d20btn")): dbArgs = json.set(dbArgs, "d20s", json.get(dbArgs, "d20s")+1)]
[if(json.contains(dbArgs,"d100btn")): dbArgs = json.set(dbArgs, "d100s", json.get(dbArgs, "d100s")+1)]
[macro("NDB@this"):dbArgs]
}]

[h, token(getSelected()): target = token.name]
[h: Init_PlayerList=getLibProperty("Init_PlayerList", "Lib:A&8")]
[h: success=0]
[h, foreach(currentPlayer,Init_PlayerList,""), code:
{
	[if(json.get(currentPlayer, "Name")==target), code:
	{
		[h: success = 1]
	};
	{}]
}]
[h: assert(success == 0, target+" Already in Init")]

[token(getSelected()), code:
{
	[h:StartCount=Speed+1d10]
	[h:Init="Name="+token.name+";MoveCount="+StartCount+";ActionCount="+StartCount]
	[h:Init=json.fromStrProp(Init)]
	[h:Init=json.set(Init,"MoveRate",'[0,1]')]
}]

[h: setLibProperty("Init_PlayerList", json.append(getLibProperty("Init_PlayerList"),Init))]

[macro("ParsePlayerInit@Lib:A&8"): 0]
[macro("Display@Lib:A&8"): 0]

[Dialog("UpdateCounts","width=300; height=350; temporary=1; input=1; noframe=0"):
{
	[r, macro("GenerateCountUpdateHTML@Lib:A&8"): macro.args]
}]
[abort(0)]

[h: roll=1d20]
[h: crit=pdice("1d6-1")]
[h: mod=macro.args+0]

[if(roll==20), code:
{
	[macro.return="total="+(roll+crit+Accuracy+mod)+"; tooltip1=Roll+Crit+Accuracy+Mod="+roll+"+"+crit+"+"+Accuracy+"+"+mod]
};
{
	[macro.return="total="+(roll+Accuracy+mod)+"; tooltip1=Roll+Crit+Accuracy+Mod="+roll+"+"+0+"+"+Accuracy+"+"+mod]
}]

[h: currentPlayer = macro.args]
[h: currentName = json.get(currentPlayer, "Name")]
[h, Token(currentName): Image=getTokenImage() ]

[h, Token(currentName): needLink = if( isOwner() || isGM(), 1, 0)]

[r, if( needLink == 1 ), code:
{
	[h: macro.return = macroLink('<img alt="'+ currentName +'" border=0 width=48 height=48 src='+ Image +"></img>", "DisplayCountUpdate@this", "gm", currentName )]
};
{
	[h: macro.return = '<img alt="'+ currentName +'" border=0 width=48 height=48 src='+ Image +"></img>"]
}]

[ dice = arg( 0 ) ]
[ sides = arg( 1 ) ]
[ roll = 0 ]
[ total = 0]
[ cntpens = 0 ]
[if(argCount()==3), code:
{
	[pen = arg(2)]
};
{
	[pen = sides]
}]

<!--Check for Lockup Loop Illegal Pen Value-->
[abort(if(pen<=1,0,-1))]

<!-- Explode Dice if they roll equal or greater than target number. -->
[for(i,0,dice,1,""), code:
{
	[roll=eval("1d"+sides)]
	[total=total+roll]
	[while(roll>=pen), code:
	{
		[roll = if(sides==20,1d6,if(sides == 100, 1d20, eval("1d"+sides) ) )]
		[total=total+roll-1]
		[ cntpens = cntpens+1 ]
	}]

}]

[ macro.return = total ]

[Frame("UpdateCounts"):
{
	[r, macro("DisplayCountUpdate@Lib:A&8"): currentToken()]
}]
