[ dice = arg( 0 ) ]
[ sides = arg( 1 ) ]
[ roll = 0 ]
[ total = 0]
[ cntpens = 0 ]
[if(argCount()==3), code:
{
	[pen = arg(2)]
};
{
	[pen = sides]
}]

<!--Check for Lockup Loop Illegal Pen Value-->
[abort(if(pen<=1,0,-1))]

<!-- Explode Dice if they roll equal or greater than target number. -->
[for(i,0,dice,1,""), code:
{
	[roll=eval("1d"+sides)]
	[total=total+roll]
	[while(roll>=pen), code:
	{
		[roll = if(sides==20,1d6,if(sides == 100, 1d20, eval("1d"+sides) ) )]
		[total=total+roll-1]
		[ cntpens = cntpens+1 ]
	}]

}]

[ macro.return = total ]

